CREATE TABLE `khachhang` (
  `MaKH` int(11) NOT NULL,
  `TenKH` varchar(30) NOT NULL,
  `sdt` varchar(15) NOT NULL,
  `DiaChi` varchar(100) NOT NULL,
  `email` varchar(40) NOT NULL,
