--
-- Cấu trúc bảng cho bảng `khoakham`
--

CREATE TABLE `khoakham` (
  `MaKhoa` int(11) NOT NULL,
  `TenKhoa` varchar(50) NOT NULL,
  `HinhAnh` varchar(100) NOT NULL,
  `GioiThieu` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

--
-- Đang đổ dữ liệu cho bảng `khoakham`
--

INSERT INTO `khoakham` (`MaKhoa`, `TenKhoa`, `HinhAnh`, `GioiThieu`) VALUES
(15, 'Tiêu hóa', 'tieu-hoa.png', '<p><strong>Tư vấn, kh&aacute;m v&agrave; điều trị c&aacute;c Bệnh Ti&ecirc;u h&oacute;a</strong></p>\r\n\r\n<ul>\r\n	<li>Ăn uống k&eacute;m, kh&ocirc;ng ngon</li>\r\n	<li>Rối loạn ti&ecirc;u h&oacute;a, táo bón, trĩ</li>\r\n	<li>Nhi&ecirc;̃m vi khuẩn HP (Helicobacter pylori)</li>\r\n	<li>N&ocirc;̣i soi dạ dày, đại tr&agrave;ng, ti&ecirc;u hóa</li>\r\n	<li>Bu&ocirc;̀n n&ocirc;n, chướng bụng, đ&acirc;̀y bụng ợ chua, đ&acirc;̀y hơi</li>\r\n	<li>Co thắt thực quản,&nbsp;H&ocirc;̣i chứng ru&ocirc;̣t kích thích</li>\r\n	<li>Đau bụng,&nbsp;dạ dày, đại tr&agrave;ng, thượng vị</li>\r\n	<li>Vi&ecirc;m đại tràng, dạ dày,&nbsp;tá tràng</li>\r\n	<li>Ung thư dạ dày, U nang tuy&ecirc;́n tụy</li>\r\n	<li>Bệnh l&yacute; về gan, mật</li>\r\n	<li>...</li>\r\n</ul>\r\n'),
(16, 'Tim mạch', 'tim-mach.png', '<p><strong>Kh&aacute;m tư vấn tim mạch</strong></p>\r\n\r\n<ul>\r\n	<li>Khó thở, Đau ngực, đau tim</li>\r\n	<li>Tăng huyết &aacute;p, hạ huyết &aacute;p</li>\r\n	<li>Rối loạn mỡ m&aacute;u, cao huy&ecirc;́t áp, chóng mặt</li>\r\n	<li>Bệnh van tim (Hẹp hở van tim),&nbsp;Hẹp đ&ocirc;̣ng mạch chủ</li>\r\n	<li>Cảm giác h&ocirc;̀i h&ocirc;̣p, tim đ&acirc;̣p nhanh &nbsp; &nbsp;</li>\r\n	<li>Tim bẩm sinh, có ti&ecirc;̀n sử b&ecirc;̣nh tim to,&nbsp;ti&ecirc;̀n sử tai bi&ecirc;́n &nbsp; &nbsp;</li>\r\n	<li>Đ&atilde; đặt stent tim,&nbsp;nong đ&ocirc;̣ng mạch vành</li>\r\n	<li>Giãn tĩnh mạch ch&acirc;n &nbsp; &nbsp;</li>\r\n	<li>...</li>\r\n</ul>\r\n'),
(30, 'Nhi khoa', 'nhi-khoa.png', '<p><strong>Kh&aacute;m v&agrave; điều trị</strong></p>\n\n<ul>\n	<li>B&ecirc;̣nh l&yacute; sơ sinh</li>\n	<li>B&ecirc;̣nh ti&ecirc;u hóa</li>\n	<li>B&ecirc;̣nh tu&acirc;̀n hoàn</li>\n	<li>B&ecirc;̣nh h&ocirc; h&acirc;́p</li>\n	<li>B&ecirc;̣nh huy&ecirc;́t học</li>\n	<li>B&ecirc;̣nh th&acirc;̣n Ti&ecirc;́t ni&ecirc;̣u</li>\n	<li>B&ecirc;̣nh th&acirc;̀n kinh</li>\n	<li>B&ecirc;̣nh ngo&agrave;i da</li>\n	<li>B&ecirc;̣nh xương khớp</li>\n</ul>\n'),
(31, 'Y Cổ Truyền', 'y-hoc-co-truyen.png', '<p><strong>Tư vấn, kh&aacute;m v&agrave; điều trị c&aacute;c vấn đề:</strong></p>\r\n\r\n<ul>\r\n	<li>Bệnh l&yacute; thần kinh: đau đầu, mất ngủ, suy nhược thần kinh...</li>\r\n	<li>Bệnh l&yacute; cơ xương khớp: đau mỏi tay ch&acirc;n, tho&aacute;i h&oacute;a khớp, vi&ecirc;m khớp...</li>\r\n	<li>Bệnh l&yacute; tim mạch: Tăng huyết &aacute;p, huyết &aacute;p thấp, đau thắt ngực...</li>\r\n	<li>Bệnh l&yacute; đường ti&ecirc;u h&oacute;a: đau bụng, rối loạn chức năng ti&ecirc;u h&oacute;a...</li>\r\n</ul>\r\n'),
(32, 'Sản phụ khoa', 'san-phu-khoa.png', '<p><strong>Kh&aacute;m v&agrave; điều trị</strong></p>\r\n\r\n<ul>\r\n	<li>R&ocirc;́i loạn kinh nguy&ecirc;̣t, ch&acirc;̣m kinh, đau bụng kinh&nbsp; &nbsp;</li>\r\n	<li>Tắc hai vòi trứng, Đa nang bu&ocirc;̀ng trứng, Chụp vòi trứng,&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</li>\r\n	<li>Khám hi&ecirc;́m mu&ocirc;̣n, v&ocirc; sinh &nbsp; &nbsp;</li>\r\n	<li>Khám Phụ Khoa &nbsp;&nbsp;&nbsp;</li>\r\n	<li>Khám thai sản &nbsp;&nbsp;&nbsp;</li>\r\n	<li>Kh&aacute;m tiền h&ocirc;n nh&acirc;n, Ti&ecirc;̀n sinh &nbsp; &nbsp;</li>\r\n	<li>Kiểm tra phụ khoa &nbsp; &nbsp;</li>\r\n	<li>Loạn dưỡng v&uacute; &nbsp;&nbsp;&nbsp;</li>\r\n	<li>Rong kinh kéo dài &nbsp; &nbsp;</li>\r\n	<li>Si&ecirc;u &acirc;m thai định kỳ &nbsp;&nbsp;&nbsp;</li>\r\n	<li>Thai lưu</li>\r\n	<li>U xơ tử cung,&nbsp;Vi&ecirc;m l&ocirc;̣ tuy&ecirc;́n</li>\r\n	<li>...</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n'),
(33, 'Thần kinh', 'than-kinh.png', '<h2><strong>Kh&aacute;m bệnh chuy&ecirc;n khoa Thần kinh</strong></h2>\r\n\r\n<ul>\r\n	<li>Bại Não &nbsp;&nbsp;&nbsp;</li>\r\n	<li>Đau đầu, ch&oacute;ng mặt, buồn n&ocirc;n&nbsp; &nbsp;</li>\r\n	<li>B&ecirc;̣nh Pakison, b&ecirc;̣nh ti&ecirc;̀n đình &nbsp; &nbsp;</li>\r\n	<li>Bị co cơ, căng d&acirc;y th&acirc;̀n kinh &nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</li>\r\n	<li>Đ&ocirc;̣ng kinh, có những cơn vãng ý thức &nbsp; &nbsp;</li>\r\n	<li>Bị t&ecirc; bì nửa mặt, chèn d&acirc;y th&acirc;̀n kinh</li>\r\n	<li>Bồn chồn, lo lắng, hồi hộp, ch&acirc;n tay run &nbsp; &nbsp;</li>\r\n	<li>Có d&acirc;́u hi&ecirc;̣u tăng đ&ocirc;̣ng &nbsp; &nbsp;&nbsp;</li>\r\n	<li>Co rút cổ, đau đầu với mặt,&nbsp;ch&acirc;n tay, vã m&ocirc;̀ h&ocirc;i&nbsp; &nbsp;</li>\r\n	<li>Chấn thương đầu, d&acirc;y thần kinh</li>\r\n	<li>...</li>\r\n</ul>\r\n\r\n<p>Ẩn bớt</p>\r\n\r\n<p>To&agrave;n quốc</p>\r\n'),
(34, 'Siêu âm thai', 'sieu-am-thai.png', '<h2><strong>Kh&aacute;m v&agrave; điều trị</strong></h2>\r\n\r\n<p>Dưới đ&acirc;y l&agrave; một số vấn đề thường gặp m&agrave; c&aacute;c b&aacute;c sĩ si&ecirc;u &acirc;m thai thường kh&aacute;m v&agrave; điều trị</p>\r\n\r\n<ul>\r\n	<li>Khám thai sản</li>\r\n	<li>Khám thai l&acirc;̀n đ&acirc;̀u</li>\r\n	<li>Si&ecirc;u &acirc;m thai định kỳ</li>\r\n	<li>Si&ecirc;u &acirc;m thai tu&acirc;̀n thứ 12</li>\r\n	<li>Si&ecirc;u &acirc;m thai tu&acirc;̀n thứ 16</li>\r\n	<li>Si&ecirc;u &acirc;m thai tu&acirc;̀n thứ 18</li>\r\n	<li>Si&ecirc;u &acirc;m thai tu&acirc;̀n thứ 22</li>\r\n</ul>\r\n'),
(35, 'Tai mũi họng', 'tai-mui-hong.png', '<p><strong>C&aacute;c bệnh Tai Mũi Họng</strong></p>\r\n\r\n<ul>\r\n	<li>&Ugrave; tai, đau tai, chảy máu tai</li>\r\n	<li>Thủng m&agrave;ng nhĩ, điếc đột ngột</li>\r\n	<li>Vi&ecirc;m tai giữa</li>\r\n	<li>Amidan, V.A</li>\r\n	<li>Vi&ecirc;m xoang</li>\r\n	<li>Nghẹt mũi</li>\r\n	<li>Hay bị chảy m&aacute;u cam</li>\r\n	<li>Đau c&ocirc;̉ họng, kh&oacute; nuốt</li>\r\n	<li>Ho kéo dài</li>\r\n	<li>Ngủ ngáy</li>\r\n	<li>...</li>\r\n</ul>\r\n'),
(36, 'Cơ Xương Khớp', 'co-xuong-khop.png', '<h2><strong>Bệnh Cơ Xương Khớp</strong></h2>\r\n\r\n<ul>\r\n	<li>Gout</li>\r\n	<li>Tho&aacute;i h&oacute;a khớp: khớp gối, cột sống thắt lưng, cột sống cổ</li>\r\n	<li>Vi&ecirc;m khớp dạng thấp, Vi&ecirc;m đa khớp, Vi&ecirc;m g&acirc;n</li>\r\n	<li>Tr&agrave;n dịch khớp gối, Tr&agrave;n dịch khớp h&aacute;ng, Tr&agrave;n dịch khớp khủy, Tr&agrave;n dịch khớp vai</li>\r\n	<li>Lo&atilde;ng xương, đau nhức xương</li>\r\n	<li>Vi&ecirc;m xương, gai xương</li>\r\n	<li>Vi&ecirc;m cơ, Teo cơ, chứng đau mỏi cơ</li>\r\n	<li>Yếu cơ, Loạn dưỡng cơ</li>\r\n	<li>C&aacute;c chấn thương về cơ, xương, khớp</li>\r\n	<li>...</li>\r\n</ul>\r\n\r\n<p>Ẩn bớt</p>\r\n\r\n<p>To&agrave;n quốc</p>\r\n');

